setup:
this.toRadians =  Math.PI / 180; 

this.rotation = 0;
this.halfWidth = context.canvas.width / 2;
this.halfHeight = context.canvas.height / 2;

draw:
context.save();
context.translate(this.halfWidth, this.halfHeight);
//context.scale(3,3);
context.rotate(this.rotation* this.toRadians);

context.font = "20px Arial";
context.fillText("Infolipo",10,50);
context.strokeText("Infolipo",10,50);
this.rotation = this.rotation + 1;

context.restore();

context.save();
context.translate(this.halfWidth, this.halfHeight);
//context.scale(3,3);
context.rotate(this.rotation* this.toRadians *3);

context.font = "20px Arial";
context.fillText("Infolipoer",10,50);
context.strokeText("Infolipoer",10,50);


context.restore();
