setup:
this.rotation = 0;
this.halfWidth = context.canvas.width / 2;
this.halfHeight = context.canvas.height / 2;


context.fillStyle = "rgb(160, 82, 45)";
context.fillRect(0, 0, context.canvas.width, context.canvas.height);

context.font = "20px Corsica";
context.fillText("Infolipo",45,75);
context.strokeText("Infolipo",45,75);

context.fillRect (60, 80, 55, 50);
context.fillStyle = (0, 0, 900, 0.9);


this.buffer = document.createElement('canvas');
this.buffer.width = context.canvas.width;
this.buffer.height = context.canvas.height;
this.bufferCtx = this.buffer.getContext("2d");
this.counter = 0;

draw:
this.counter += 1;
console.log(this.counter);

//context.drawImage(this.v, 0, 0, context.canvas.width, context.canvas.height);
this.bufferCtx.drawImage(context.canvas, 0, 0, context.canvas.width, context.canvas.height);



context.save();
context.translate(this.halfWidth, this.halfHeight);
//context.scale(3,3);
context.rotate(this.rotation* this.toRadians/2);

if(this.counter < 350){
 context.drawImage(this.buffer, 8,13, context.canvas.width/2, context.canvas.height);
}else if(this.counter < 600){
    context.drawImage(this.buffer, 16,20, context.canvas.width, context.canvas.height);
}else{
    context.drawImage(this.buffer, 6,9, context.canvas.width/2, context.canvas.height);
}
this.rotation = this.rotation + 3;

context.restore();
